actionConfiguration.body:
{{JSON.stringify({
	query: `
  mutation createEconomicEvent(
    $action: ID!
    $provider_id: ID!
    $receiver_id: ID!
    $product_name: String!
    $tags: ID
    $notes: String
  ) {
    createEconomicEvent(
      event: {
        action: $action
        provider: $provider_id
        receiver: $receiver_id
        resourceQuantity: null
      }
      newInventoriedResource: { name: $product_name, note: $notes, tags: [$tags] }
    ) {
      economicEvent {
        id
        note
        resourceInventoriedAs {
          id
          name
          onhandQuantity {
            hasNumericalValue
            hasUnit {
              label
              symbol
            }
          }
          accountingQuantity {
            hasNumericalValue
            hasUnit {
              label
              symbol
            }
          }
        }
      }
    }
  }

          ',
          `,
	variables: 
			{
        "action": "produce",
        "provider_id": "01FWFJZ142TMT6TXP6Z5Y05R6F",
        "receiver_id": "01FWFJZ142TMT6TXP6Z5Y05R6F",
        "product_name": {{Insert_Name.text}},
        "tags": "tags",
        "notes": {{Insert_Note.text}}
        }
})}}

