template:
mutation {
  createResourceSpecification(
    resourceSpecification: {
      name: "{{payload}}"
      resourceClassifiedAs: "pane"
      note: "nota pane"
    }
  ) {
    resourceSpecification {
      id
      name
    }
  }
}

mutation {createEconomicEvent(
      event: {
        action: "produce",
        provider: "{{provider}}",
        receiver: "{{receiver}}",
        resourceQuantity: null,
      },
      newInventoriedResource: {
        name: "{{payload}}",
        note: "notes",
        tags: "tag"
      }
      ) {
        economicEvent {
          id
          note
          resourceInventoriedAs {
            id
            name
            onhandQuantity {
              hasNumericalValue
              hasUnit {
                label
                symbol
              }
            }
            accountingQuantity {
              hasNumericalValue
              hasUnit {
                label
                symbol
              }
            }
          }
        }
      }
    }

mutation {

  login(emailOrUsername: "{{emailOrUsername}}", password: "{{password}}") {
      token # Use the token to activate a session with the server
    }
}

query myAgent {
  myAgent {
    agentType
    canonicalUrl
    inventoriedEconomicResources {
      name
